width = 25
height = 6

-- chunksOf
layers :: Text -> [Text]
layers input = Text.chunksOf (width*height) input

fewest0 :: [Text] -> Text
fewest0 ls = minimumBy cmp ls
    where 
        cmp a b = Text.count "0" a `compare` Text.count "0" b

findCorruption :: Text -> Int
findCorruption layer = (Text.count "1" layer) * (Text.count "2" layer)
 
day8 :: Text -> Int
day8 input = findCorruption . fewest0 $ layers input